import { useMemo, useState } from "react";

import "./App.css";
import {
  AppBar,
  Box,
  Button,
  Card,
  CardContent,
  CardMedia,
  Checkbox,
  Chip,
  Container,
  createTheme,
  CssBaseline,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormControl,
  FormControlLabel,
  Grid,
  InputAdornment,
  InputLabel,
  MenuItem,
  Rating,
  Select,
  Switch,
  TextField,
  ThemeProvider,
  Toolbar,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { Add, DarkMode, LightMode, Search, Sort } from "@mui/icons-material";

function App() {
  const initialProducts = [
    {
      id: 1,
      name: "Wireless Headphones",
      category: "Electronics",
      description:
        "Experience crystal-clear sound and deep bass with these over-ear wireless headphones. Perfect for travel, work, or workouts.",
      price: 5000,
      inStock: true,
      quantity: 1,

      image:
        "https://ptron.in/cdn/shop/products/B07XL8HFNC.MAIN.jpg?v=1667656208",
    },
    {
      id: 2,
      name: "Coffee Mug",
      description:
        "Start your day with this elegant ceramic coffee mug. Durable, microwave-safe, and designed for everyday comfort.",
      category: "Home",
      price: 250,
      inStock: true,
      quantity: 1,

      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 3,
      name: "Running Shoes",
      category: "Apparel",
      description:
        "Start your day with this elegant ceramic coffee mug. Durable, microwave-safe, and designed for everyday comfort.",
      price: 800,
      inStock: false,
      quantity: 1,

      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 4,
      name: "Bluetooth Speaker",
      category: "Electronics",
      price: 3000,
      inStock: true,
      description:
        "Enjoy powerful sound wherever you go. Compact and water-resistant, this Bluetooth speaker is ideal for indoor or outdoor fun.",
      quantity: 1,

      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 5,
      name: "Desk Lamp",
      category: "Home",
      price: 500,
      inStock: false,
      quantity: 1,
      description:
        "Illuminate your workspace with this modern LED desk lamp. Adjustable neck and soft light for reduced eye strain.",

      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 6,
      name: "Smartwatch",
      category: "Electronics",
      price: 2200,
      inStock: true,
      quantity: 1,
      description:
        "Track your fitness, monitor your sleep, and stay connected with this stylish smartwatch built for everyday life.",

      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 7,
      name: "Backpack",
      category: "Apparel",
      price: 100.99,
      inStock: true,
      quantity: 1,
      description:
        "Carry your essentials comfortably with this waterproof backpack. Designed with multiple compartments and modern style.",

      image:
        "https://images.unsplash.com/photo-1506784983877-45594efa4cbe?auto=format&fit=crop&w=400&q=80",
    },
    {
      id: 8,
      name: "Table Clock",
      category: "Home",
      price: 800.99,
      inStock: true,
      quantity: 1,
      description:
        "A classic analog clock with silent movement for peaceful sleep and stylish home decor.",

      image:
        "data:image/jpeg;base64,",
    },
  ];

  const [products, setProducts] = useState(initialProducts);
  const [searchProductName, setSearchProductName] = useState("");
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [form, setForm] = useState({
    name: "",
    category: "",
    price: "",
    inStock: true,
    description: "",
  });
  const [categoryFilter, setCategoryFilter] = useState("All");
  const [sortOrder, setSortOrder] = useState("none");
  const [darkMode, setDarkMode] = useState(false);

  const prefersMobile = useMediaQuery("(max-width:600px)");

  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode: darkMode ? "dark" : "light",
        },
        components: {
          MuiCard: { defaultProps: { elevation: 3 } },
        },
      }),
    [darkMode]
  );

  const categories = useMemo(() => {
    const set = new Set(products.map((p) => p.category));
    return ["All", ...Array.from(set)];
  }, [products]);

  const visibleProducts = useMemo(() => {
    let list = [...products];
    if (categoryFilter !== "All") {
      list = list.filter((p) => p.category === categoryFilter);
    }
    if (searchProductName.trim() !== "") {
      const search = searchProductName.trim().toLowerCase();
      list = list.filter((p) => p.name.toLowerCase().includes(search));
    }
    if (sortOrder === "asc") {
      list.sort((a, b) => a.price - b.price);
    } else if (sortOrder === "desc") {
      list.sort((a, b) => b.price - a.price);
    }

    return list;
  }, [products, categoryFilter, sortOrder, searchProductName]);

  const openAdd = () => {
    setForm({ name: "", category: "", price: "", inStock: true });
    setIsAddOpen(true);
  };

  const handleAddClose = () => {
    setIsAddOpen(false);
  };

  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleAddSubmit = (e) => {
    e.preventDefault();
    const { name, category, price, inStock, description } = form;
    if (
      !name.trim() ||
      !description.trim() ||
      !category.trim() ||
      price === ""
    ) {
      alert("Please fill Name, Category and Price");
      return;
    }
    const newProduct = {
      id: Date.now(),
      name: name.trim(),
      category: category.trim(),
      price: parseInt(price),
      inStock: Boolean(inStock),
      description: description.trim(),
    };
    setProducts((prev) => [newProduct, ...prev]);
    setIsAddOpen(false);
  };

  const toggleSort = () => {
    setSortOrder((s) => (s === "none" ? "asc" : s === "asc" ? "desc" : "none"));
  };
  return (
    <>
      <ThemeProvider theme={theme} sx={{ pr: { xs: 2, md: 0 } }}>
        <CssBaseline />
        <AppBar position="sticky" sx={{ background: "#074276", width: "100%" }}>
          <Toolbar
            sx={{
              display: "flex",
              justifyContent: "space-between",
              m: 0,
              p: 0,
            }}
          >
            <Typography variant="h6">Product Dashboard</Typography>

            <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
              <FormControlLabel
                control={
                  <Switch
                    checked={darkMode}
                    onChange={() => setDarkMode((m) => !m)}
                  />
                }
                label={darkMode ? <DarkMode /> : <LightMode />}
              />
              <Button
                startIcon={<Add />}
                variant="contained"
                onClick={openAdd}
                sx={{
                  textTransform: "none",
                  borderRadius: "50px",
                  background: "linear-gradient(to bottom, #7474bf, #348ac7);",
                }}
              >
                Add Product
              </Button>
            </Box>
          </Toolbar>
        </AppBar>
        <Container sx={{ mt: 4, mb: 6 }}>
          <Box sx={{ mb: 2 }}>
            <Box
              sx={{
                display: "flex",
                justifyContent: "flex-end",
                gap: "10px",
                flexWrap: "wrap",
              }}
            >
              <TextField
                label="Search by Product Name"
                variant="standard"
                value={searchProductName}
                onChange={(e) => setSearchProductName(e.target.value)}
                sx={{ width: "220px" }}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Search />
                    </InputAdornment>
                  ),
                }}
              />
              <FormControl fullWidth sx={{ width: "220px" }}>
                <InputLabel>Filter by Category</InputLabel>
                <Select
                  label="Filter by Category"
                  variant="standard"
                  value={categoryFilter}
                  onChange={(e) => setCategoryFilter(e.target.value)}
                >
                  {categories.map((c) => (
                    <MenuItem value={c} key={c}>
                      {c}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>

              <Button
                variant={sortOrder === "none" ? "outlined" : "contained"}
                onClick={toggleSort}
                startIcon={<Sort />}
                sx={{ textTransform: "none" }}
              >
                {sortOrder === "none"
                  ? "Sort by Price"
                  : sortOrder === "asc"
                  ? "Price: Low  High"
                  : "Price: High  Low"}
              </Button>
            </Box>
          </Box>
          <Box
            sx={{
              display: "flex",
              justifyContent: { xs: "center", md: "space-around" },
              gap: "10px",
              flexWrap: "wrap",
              width: "100%",
            }}
          >
            {visibleProducts.map((p) => (
              <Box>
                <Card
                  // key={p.id}
                  elevation={10}
                  sx={{
                    height: "100%",
                    display: "flex",
                    width: prefersMobile ? "90vw" : "18rem",
                    flexDirection: "column",
                    borderRadius: "1rem",
                    transition: "0.5s",
                    background: "rgba(255, 255, 255, 0.15)",
                    border: "1px solid rgba(255, 255, 255, 0.3)",
                  }}
                >
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Box sx={{ background: "#074276", height: "4rem" }}>
                      <Box sx={{ background: "white", position: "relative" }}>
                        <Box
                          sx={{
                            display: "flex",
                            gap: "5px",
                            position: "absolute",
                            left: "1rem",
                            top: "1rem",
                          }}
                        >
                          <Typography
                            sx={{
                              color: "white",
                              fontWeight: 600,
                              fontSize: "1rem",
                            }}
                          >{`Name :`}</Typography>

                          <Typography
                            sx={{
                              color: "white",

                              fontWeight: 600,
                              fontSize: "1rem",
                            }}
                          >
                            {p.name}
                          </Typography>
                        </Box>
                      </Box>
                    </Box>
                    <CardMedia
                      component="img"
                      height="200"
                      image={p.image}
                      alt={p.name}
                    />
                    <Typography sx={{ mt: 1, mb: 1, textAlign: "start" }}>
                      {p.description}
                    </Typography>
                    <Typography
                      sx={{ mt: 1, textAlign: "start", fontWeight: "bold" }}
                    >
                      Price: {p.price.toFixed(2)}
                    </Typography>
                    <Box
                      sx={{
                        display: "flex",
                        justifyContent: "start",
                        alignContent: "start",
                        mt: 1,
                      }}
                    >
                      <Rating
                        name="half-rating"
                        defaultValue={2.5}
                        precision={0.5}
                      />
                    </Box>
                    <Box
                      sx={{
                        mt: 2,
                        display: "flex",
                        alignItems: "center",
                        gap: 1,
                      }}
                    >
                      <FormControlLabel
                        control={<Checkbox checked={p.inStock} disabled />}
                        label={p.inStock ? "In Stock" : "Out of Stock"}
                      />
                    </Box>
                  </CardContent>
                </Card>
              </Box>
            ))}

            {visibleProducts.length === 0 && (
              <Grid item xs={12}>
                <Box sx={{ p: 4, textAlign: "center" }}>
                  <Typography>
                    No products match the selected filters.
                  </Typography>
                </Box>
              </Grid>
            )}
          </Box>
        </Container>

        <Dialog open={isAddOpen} onClose={handleAddClose} fullWidth>
          <form onSubmit={handleAddSubmit}>
            <DialogTitle>Add Product</DialogTitle>
            <DialogContent>
              <Box
                sx={{ mt: 1, display: "flex", flexDirection: "column", gap: 2 }}
              >
                <TextField
                  label="Name"
                  variant="standard"
                  name="name"
                  value={form.name}
                  onChange={handleFormChange}
                  required
                  fullWidth
                />

                <TextField
                  label="Category"
                  name="category"
                  variant="standard"
                  value={form.category}
                  onChange={handleFormChange}
                  required
                  fullWidth
                  helperText="e.g. Electronics, Home, Apparel"
                />
                <TextField
                  label="Description"
                  variant="standard"
                  name="description"
                  value={form.description}
                  onChange={handleFormChange}
                  required
                  fullWidth
                />
                <TextField
                  label="Price"
                  name="price"
                  type="number"
                  variant="standard"
                  inputProps={{ step: "0.01", min: 0 }}
                  value={form.price}
                  onChange={handleFormChange}
                  required
                  fullWidth
                />

                <FormControlLabel
                  control={
                    <Checkbox
                      name="inStock"
                      checked={form.inStock}
                      onChange={handleFormChange}
                    />
                  }
                  label="In Stock"
                />
              </Box>
            </DialogContent>

            <DialogActions>
              <Button onClick={handleAddClose} color="error">
                Cancel
              </Button>
              <Button
                type="submit"
                variant="contained"
                sx={{ borderRadius: "50px" }}
              >
                Add Product
              </Button>
            </DialogActions>
          </form>
        </Dialog>
      </ThemeProvider>
    </>
  );
}

export default App;
